package com.github.nicejing.security.security.support;

import lombok.Data;
import org.springframework.boot.context.properties.ConfigurationProperties;
import org.springframework.stereotype.Component;

/**
 * @author Nathan
 */
@ConfigurationProperties(prefix = "security")
@Component
@Data
public class AdminSecurityProperties {

    private String tokenName                  = "access_token";

    private String superAdminRoleName         = "SUPER_ADMIN";

    private String rsaPublicKeyModulus        = "";

    private String rsaPublicKeyPublicExponent = "65537";

    private String privateExponent            = "";

    private String loginProcessingUrl         = "/login";

    private String logoutProcessingUrl        = "/logout";

    private String adminRedisNameSpace        = "supplychain:admin:";

    public Integer adminTokenExpire           = 10;

}
